<?php

namespace igk\tools\webscrapper\Tests;

use Closure;
use IGK\System\Html\Dom\HtmlBodyNode;
use IGK\System\Html\Dom\HtmlDocumentNode;
use IGK\System\Html\Dom\HtmlHeadNode;
use IGK\System\Html\WebDocument;
use IGK\System\Http\IHttpClient;
use IGK\System\Http\IHttpClientOptions;
use IGKException;
use IGKObject;

class WebScrapperTestHttpClient
extends IGKObject
implements IHttpClient
{
    private $m_responseHeaders = [];
    private $m_responseStatus = 200;
    private $m_datas = [
        'pages/about.html' => "<!DOCTYPE html>the about page----<a href=\"../index.html\">Home</a><a href=\"./current\">current relative</a>" .
            "<a href=\"current\">current short</a>",
        'pages/current.html' => 'the current page .... ',
        'pages/index' => 'idnex Page.',
        'favicon.ico' => 'data:image/jpeg;base64,',
    ];
    private $m_responsesData = [];

    public function getData()
    {
        return $this->m_datas;
    }
    /**
     * chain set data
     * @param null|array $value 
     * @return $this 
     */
    public function setData(?array $value)
    {
        $this->m_datas = $value;
        return $this;
    }
    /**
     * 
     * @var bool
     */
    var $followLocation = false;

    public function getRequestHeaderResponse(): ?array
    {
        return $this->m_responseHeaders;
    }
    public function __construct()
    {
        $this->m_datas['pages/index'] = function ($args, $uri) {
            // $doc = new WebDocument; 
            $doc = new WebDocument;
            $doc->title = "Index page";
            $doc->body->Content = "The Content page of : " . $uri;
            return $doc->render();
        };
    }

    public function download(string $url, IHttpClientOptions $options)
    {
    }

    public function get(string $url)
    {
    }

    public function post(string $url, array $data = [])
    {
    }

    /**
     *
     * @param string $url start with "/" or "http://" 
     * @return mixed 
     * @throws IGKException 
     */
    public function request(string $url)
    {
        //$url = ltrim($url,'/');
        $exts = ['', '.html'];
        $surl = $url;
        $args = [];
        $p = explode("?", $url, 2);
        $ps = [$url];
        if (count($p) > 1) {
            $surl = $p[0]; //[]
            $query = igk_getv(explode('#', $p[1]), 0);
            parse_str($query, $args);
            $ps[] = $surl;
        }
        while (count($ps) > 0) {

            $surl = array_shift($ps);

            while (count($exts) > 0) {
                $ext = array_shift($exts);
                $url = $surl . $ext;
                if ($m = igk_getv($this->m_datas, $url)) {
                    if ($m instanceof Closure) {
                        $m = $m($args, $url); // passing parameter and request url
                    }
                    $this->m_responseStatus = 200;
                    $this->m_responseHeaders = igk_getv($this->m_responsesData, $url) ?? [
                        'Content-Type' => 'text/html'
                    ];
                    if (preg_match("/data:/", $m)) {
                        $rf = explode(",", $m, 2);
                        if (str_ends_with($rf[0], 'base64')) {
                            return base64_decode($rf[1]);
                        }
                        return $rf[1];
                    }

                    return $m;
                }
            }
            $this->m_responseStatus = 404;
            $this->m_responseHeaders = ['Content-Type' => 'text/plain'];
            return null;
        }
    }

    /**
     * get the last request status
     * @return int 
     */
    public function getStatus(): int
    {
        return $this->m_responseStatus;
    }
}
